import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { deleteObject } from "../../Redux/actions/objectActions";
import { useHistory } from "react-router-dom";
import Details from "./details";
import Select from "./select";
import useModal from "use-react-modal";

export default function Table(props) {
  const [limit, setLimit] = useState(100);
  const dispatch = useDispatch();
  const history = useHistory();
  const [ID, setID] = useState();
  const { isOpen, openModal, closeModal, Modal } = useModal();

  // Edit Object

  const editObject = id => {
    history.push("/editObj/" + id);
  };

  // Delete Object

  const removebject = id => {
    if (
      window.confirm(
        "Are you sure you want to delete that? There's no going back!"
      )
    ) {
      window.location.reload(true);
      dispatch(deleteObject(id));
    }
  };

  // handleClick

  const handleClick = (e, number) => {
    setID(number);
    console.log(ID);
    openModal(e);
  };

  // handling number of shown products

  const chooseAmount = e => {
    setLimit(e.target.value);
  };

  return (
    <div>
      {props.data.slice(0, limit).map(row => (
        <div
          onClick={e => handleClick(e, row.id)}
          className="main_box"
          key={row.id}
        >
          <div className="vinyl">
            <div className="middle_circle"></div>
          </div>
          <div className="empty">
            <img className="album_image" alt="No cover" src={process.env.PUBLIC_URL + row.image} />
          </div>
          <div className="titles">
            {/* <Details /> */}
            <div className="artist">{row.artist}</div>
            <div className="title">{row.title}</div>
          </div>

          {isOpen && (
            <Modal>
              <button onClick={(e) => closeModal(e)}>close</button>
              <Details id={ID} />
            </Modal>
          )}
          {/* more info */}
          {/* <div className='buttondiv'>
                        <button className='editbutton' onClick={() => {editObject(row.id)}}>Edit</button>
                        <button className='delbutton' onClick={() => {removebject(row.id)}}>Delete</button>
                    </div> */}
        </div>
      ))}
      {/* Show on the page: <Select chooseAmount={chooseAmount} /> */}
    </div>
  );
}
