import React, { useState, useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { fetchObjects, deleteObject } from "../../Redux/actions/objectActions"
import SearchBar from './searchBar'
import Table from './singleObject'
// import SearchClick from '../Header/buttons/searchButton'

export default function ObjectList() {
    
    const {searchUpdate} = SearchClick();
    const [search, setSearch] = useState('')   

    const objects = useSelector(state => state.objects.listOfObjects)
    const dispatch = useDispatch()
   
    
    useEffect(() => {
        dispatch(fetchObjects());
      }, []);

      const SearchClick = () => {
    
        const [searchUpdate, setUpdate] = useState(false);
    
        const handleClick = e => {
            setUpdate(!searchUpdate)
        };
    
        return {searchUpdate, handleClick}
    }
    
    
    // Search bar
    
    const searchedObjects = objects.filter(object => {
        return object.title.toLowerCase().includes(search)
    })
    
    const handleInput = e => 
    setSearch(e.target.value)
    console.log(searchUpdate)
    
    return(
        <div>
            {searchUpdate ? <SearchBar handleInput={handleInput} /> : <h1>see me?</h1>}
            <Table data={searchedObjects} />
        </div>
    )
}
